import { Component, OnInit } from '@angular/core';
import { NavController, AlertController } from "@ionic/angular";
import { AngularFireAuth } from "@angular/fire/auth";
import { AngularFirestore } from "@angular/fire/firestore";
import { AngularFireStorage } from "@angular/fire/storage";
@Component({
  selector: 'app-signup',
  templateUrl: './signup.page.html',
  styleUrls: ['./signup.page.scss'],
})
export class SignupPage implements OnInit {
  loaderText: string = "Creating User...";
  percent: any = '';
  resultSrc: any = 'data:image/png;base64,';
  uname: string;
  email: string;
  pwd: string;
  errMsg: string;
  constructor(
    public af: AngularFireAuth,
    public fs: AngularFirestore,
    public nav: NavController,
    public alertCtrl: AlertController,
    public storage: AngularFireStorage

  ) { }


  ngOnInit() {
  }
  async presentAlert() {
    const errorAlert = await this.alertCtrl.create({
      header: 'Oops',
      message: this.errMsg,
      buttons: [
        { text: 'ok' }
      ]
    })
    await errorAlert.present();
  }

  selectFile() {
    let file = (<HTMLInputElement>document.getElementById('file')).files[0];
    if (file) {

      let reader = new FileReader();
      reader.onload = (e) => {
        this.resultSrc = reader.result;
      }
      reader.readAsDataURL(file);
    }
    else {
      this.resultSrc = 'data:image/png;base64,';
    }
  }


  signup() {
    document.getElementById('signupLoader').style.display = "block";
    this.af.auth.createUserWithEmailAndPassword(this.email, this.pwd).then(userData => {
      this.loaderText = 'Uploading Profile Picture...';
      let task = this.storage.ref('ProfilePicture/' + userData.user.uid).putString(this.resultSrc, 'data_url');
      task.snapshotChanges().subscribe(data => {
        this.percent = Math.floor((data.bytesTransferred / data.totalBytes) * 100) + '%';
      });
      task.then(() => {
        this.percent = '';
        this.loaderText = 'Setting Up...';
        this.storage.ref('ProfilePicture/' + userData.user.uid).getDownloadURL().subscribe(url => {
          this.fs.collection('users').doc(userData.user.uid).set({
            Name: this.uname,
            Email: this.email,
            Photo: url,
            Bio: 'Hey there,I am a new user',
            UID:userData.user.uid,
          }).then(() => {
            this.af.auth.currentUser.updateProfile({
              displayName: this.uname,
              photoURL: url,
            }).then(() => {
              this.nav.navigateRoot('/home');
            }).catch(err => {
              document.getElementById('signupLoader').style.display = "none";
              alert('Try Again');
            })
          }).catch(err => {
            document.getElementById('signupLoader').style.display = "none";
            alert('Try Again');
          })
        })
      }).catch(err => {
        document.getElementById('signupLoader').style.display = "none";
        alert('Try Again');
      })
    }).catch(err => {
      document.getElementById('signupLoader').style.display = "none";
      this.errMsg = err.message;
      this.presentAlert();
    })

  }

  goto_login() {
    this.nav.pop();
  }

}
